	num[1] = i + 1;
	num[2] = a - i + 1;//前两个字符的位置满足表达式
	int count;
	for (int k = 1; num[k] <= 32; ++ k) {//求出一共几个字符count，每个字符的位置为num[k]的值
		num[k + 2] = num[k] + a;
		//if (num[k + 2] == num[k + 1]) num[k + 2] == num[k + 1] + a;
		if (num[k] == num[k + 1]) num[k + 1] = num[k] + a;
		count = k;
	}
	for (int j = 1; j < count + 1; ++ j) {
		sum = sum + factorial(num[j]);//求出表示一行字符的整数
	}
	return sum;
}

