			char temp1[100];
			char temp2[100];
			for(int i = 0; i < 100; ++ i){
				temp1[i] = text1[i];
				temp2[i] = text2[i];
			}
			temp1[long1 - 1] = 0;
			temp2[long2 - 1] = 0;
			struct Result a = LCS(temp1, text2);
			struct Result b = LCS(text1, temp2);
			int longa = strlen(a.LCS);
			int longb = strlen(b.LCS);
			if(longa >= longb){
				final = a;
			}else{
				final = b;
			}
		}
	}
	return final;
}

int i = 1;
int getMultiplications(int j,int *dimensions){
	if(j == 1){
		return 0;
	}
	int before = 0x7fffffff;
	for(int i = 1; i < j; ++ i){
		int *p;
		p = &dimensions[i];
		int now = getMultiplications(i, dimensions) + getMultiplications(j - i, p) + dimensions[0] * dimensions[i] * dimensions[j];
		if(now < before){
			before = now;
		}
	}
	return before;
}
