int gcd(int x, int y){
	int i = (x < y)? x: y;
	int k = 1;
	for (; k != 0 ; -- i) {
		k = x % i + y % i;
	}
	return i + 1;
}

int lcm(int x, int y){
	int m = (x > y)? x: y;
	int k = 1;
	for (; k != 0 ; ++ m) {
		k = m % x + m % y;
	}
	return m - 1;
}

