	for (int i = 0; i <100 ; ++ i) {
		hex[i]=0;
	}
	while(dec[i]!='\0'){
		x=x*10+dec[i]-'0';
		i++;
	}
	i=0;
	int j=x,add;
	while(j>0){
		j/=16;
		i++;
	}
	for(i;i>=1;i--){
		add=x%16;
		if(add<10){
			hex[i-1]=add+'0';
		}
		else{
			hex[i-1]=add-10+'A';
		}
		x/=16;
	}
}

