void decToHex(const char dec[], char hex[]){
	int number =0;
	int array[100];
	for (int i = 0; 1 ; ++ i){
		if(dec[i] == 0)break;
		number = number*10 + dec[i] - '0';
	}
	int j = 0;
	for (; 1 ; ++ j){
		array[j] = number % 16;
		number /= 16;
		if(number == 0)break;
	} 
	for (int i = 0; i < j+1 ; ++ i){
		if(array[i] > 9)hex[j-i] = array[i] + 55;
		else hex[j-i] = array[i] + '0';
	} 
	hex[j+1] = 0;
}

