int parseHex(const char hexString[]){
	int temp = 0;
	int i = 0;
	while(1){
		if(hexString[i]>='0'&&hexString[i]<='9'){
			temp = (temp<<4)+hexString[i]-'0';
		}else if(hexString[i]>='A'&&hexString[i]<='F'){
			temp = (temp<<4)+hexString[i]-'0'-7;
		}
		if(hexString[i]==0){
			break;
		}
		i++;
	}
	return temp;
}

