int compare(char a[],char b[]){
	int i = 0;
	while(a[i]==b[i]&&a[i]!=0&&b[i]!=0){
		i++;
	}
	return (a[i]>b[i])?1:0;
}

void sort_words(char * words[],int num_words){
		for (int i = 0; i < num_words-1; i++){
			for (int j = num_words-1; j > 0; j--){
				if(compare(words[j-1],words[j])){
					char* p =words[j-1];
					words[j-1]=words[j];
					words[j]=p;
				}
			}
		}
}

