int memony[100]={0};
int getMaxCuttingPrice(int prices[][2],int priceCount,int rodLength){
	if(memony[rodLength]>0){return memony[rodLength];}
	int max_prices=(rodLength<=priceCount ? prices[rodLength-1][1] : 0);
	for (int i = 0; i <rodLength-1 ; ++ i) {
		int cut=i+1;
		int sum=getMaxCuttingPrice(prices,priceCount,cut)
			+getMaxCuttingPrice(prices,priceCount,rodLength-cut);
		max_prices=MAX(sum,max_prices);
	}
	memony[rodLength]=max_prices;
	return max_prices;
}
