}
int isPrintable(char c){
	int flag=0;
	if(c>='!'&&c<='~'){
		flag=1;
	}
	return flag;
}
int isGraphical(char c){
	int flag=0;
	if(isPrintable(c)- isSpace(c)){
		flag=1;
	}
	return flag;
}
int isPunctuation(char c){
	int flag=0;
	if(isGraphical(c)- isAlphabitOrNumber(c)){
		flag=1;
	}
	return flag;
}

