	int max=(lenth<=6?prices[lenth-1][1]:0);
	for (int i = 0; i < lenth-1; ++ i)
	{
		int cut=i+1;
		int p=getMaxCuttingPrice(prices,priceCount,cut)+getMaxCuttingPrice(prices,priceCount,lenth-cut);
