int LCS(char text1[],char text2[])
{
	int x=strlen(text1),y=strlen(text2),n=0;
	while(x>0&&y>0)
	{
		if(text1[x-1]==text2[y-1])n++,x--,y--;
		else
		{
			char text3[x],text4[y];
			strcpy(text3,text1);
			strcpy(text4,text2);
			text3[x-1]=0,text4[y-1]=0;
			if(LCS(text3,text2)>LCS(text1,text4))
			{
				n=LCS(text3,text2);
				x--;
			}
			else
			{
				n=LCS(text1,text4);
				y--;
			}
		}
	}
	return n;
}

