int getMultiplications(int num,int dim[])
{
	if(num==1||num==0)return 0;
	if(num==2)return dim[0]*dim[1]*dim[2];
	int min=99999;
	for (int i = 1; i <num ; ++ i) 
	{
		int a[i+1],b[num-i+1];
		cpy(dim,a,1,i-1);
		cpy(dim,b,i+1,num-i);
		int x=getMultiplications(i-2,a)+getMultiplications(num-1-i,b)+dim[0]*dim[i]*dim[num];
		if(x<min)min=x;
	}
	return min;
}

