	for (int k = 1; num[k] <= 32 ; ++ k) {
	num[k+2] = num[k] + a;
	if (num[k+2] == num[k+1]) num[k+2] = num[k+1] + a;
	if (num[k] == num[k+1]) num[k+1] = num[k] + a;
