	int x;
	for(int L=2;L<=n;L++){//相乘矩阵的个数 
		for(int i=1;i<=n;i++){ 
			int j=i+L-1;
			x = j;
			if(j>n) break; 
			for(int k=i;k<j;k++){//遍历切割位置
				int min_=dp[i][j];
				int temp=dp[i][k]+dp[k+1][j]+dimensions[i-1]*dimensions[k]*dimensions[j];
				if(temp<min_){
					dp[i][j]=temp;
					min_=temp;
				}
			//	dp[i][j]=min(dp[i][j],dp[i][k]+dp[k+1][j]+p[i-1]*p[k]*p[j]);
			}
		}
	}
	return dp[1][n];
}
