void decToHex(const char dec[], char hex[])
{
    for (int i = 0; i < 99; i++)
    {
        hex[i] = 0;
    }
    int quotient = atoi(dec);
    int remainder = 0, j = 0;
    while (quotient)
    {
        remainder = quotient % 16;
        if (remainder < 10)
            hex[j++] = 48 + remainder;
