int gcd(int num1, int num2) {
	int tmp;
	// ensure num1 > num2
	if(num1 < num2) {
		tmp = num1;
		num1 = num2;
		num2 = tmp;
	}
	int r = num1 % num2;
	while(r) {
		num1 = num2;
		num2 = r;
		r = num1 % num2;
	}
	return num2;
}

