	if(num1 < num2) {
		tmp = num1;
		num1 = num2;
		num2 = tmp;
	}
	int r = num1 % num2;
	while(r) {
		num1 = num2;
		num2 = r;
		r = num1 % num2;
	}
	return a * b / num2;
}

