bool isUpper(char c) {
    int n = c - 'A';
    if(n >= 0  && n <= 25)
        return true;
    else
        return false;
}

bool isAlphabit(char c) {
    if(isLower(c) || isUpper(c))
        return true;
    else
        return false;
}

bool isAlphabitOrNumber(char c) {
    if(isAlphabit(c) || isDigit(c))
        return true;
    else
        return false;
}

bool isSpace(char c) {
    if(c == ' ' || c == '\t' || c == '\r' || c == '\n')
        return true;
    else
        return false;
}

bool isPrintable(char c) {
    if(c >= '!' && c <= '~')
        return true;
    else
        return false;
}

bool isGraphical(char c) {
    if(isPrintable(c) && !isSpace(c))
        return true;
    else
        return false;
}

bool isPunctuation(char c) {
    if(isGraphical(c) && !isAlphabitOrNumber(c))
        return true;
    else
        return false;
}

