int get_bits(int i, int lines) {
    int sum = 0;
    int pos = 31 - i;
    int right = 2 * i;
    int left = (lines - 1) * 2 - 2 * i;
    int leftflag = 1;
    int rightflag = 0;
    sum = sum + (1 << pos);
    while(pos > 0) {
        if(leftflag && left) {
            pos -= left;
            if(pos < 0 )
                break;
            sum = sum + (1 << pos);
        }
        else if(rightflag && right) {
            pos -= right;
            if(pos < 0 )
                break;
            sum = sum + (1 << pos);
        }
        else {
            pos = pos - (right + left);
            if(pos < 0 )
                break;
            sum = sum + (1 << pos);
        }
        leftflag = !leftflag;
        rightflag = !rightflag;
    }
    return sum;
}

