    int len = strlen(dec);
    int digits = 0;
    char tmp[100];
    for(int i = 0; i < len; i++)
        value = value * 10 + dec[i] - '0';
    while(value) {
        int res = value % 16;
        if(res <= 9)
            tmp[digits] = '0' + res;
        else
            tmp[digits] = 'A' + res - 10;
        digits++;
        value /= 16;
    }
    tmp[digits] = 0;
    for(int i = 0; i < digits; i++) {
        hex[i] = tmp[digits - i - 1];
    }
    hex[digits] = 0;
}

