int parseHex(const char hexString[]){
	int decimal_integer = 0;
	for (;; ++ hexString){
		int a = * hexString;
		if (a >= 'A' && a <= 'F'){
			decimal_integer = decimal_integer * 16 + (a - 'A' + 10);
		}
		if(a >= '0' && a <= '9'){
			decimal_integer = decimal_integer * 16 + (a - '0');
		}	
		if(a == 0)
			return decimal_integer;
	} 
}

