void decToHex(const char dec[], char hex[]){
	int decimal_num = 0;
	int count = 0;
	int converse[100];
	for (int i = 0; i < 100; ++ i) 
		hex [i] = 0;
	for ( ; ; ++ dec){
		int a = * dec;
		if(a == 0)
			break;
		decimal_num = decimal_num * 10 + (a - '0');
	} 
	while (decimal_num != 0){
		converse[count] = decimal_num % 16;
		count++;
		decimal_num /= 16;
	}
	for (int i = 0; i <= count - 1; ++i) {	
		hex[i] = converse[count - 1 - i];
		if (hex[i] >= 0 && hex[i] <= 9){
			hex[i] = hex[i] + '0';
		}
		if (hex[i] >= 10 && hex[i] <= 15){
			hex[i] = hex[i] + 'A' - 10;
		}
	}
}

void decToBin(const char dec[], char bin[]){
	int decimal_num = 0;
	int converse[100] = {0};
	int count = 0;
	for (int i = 0; i < 100; ++ i)
		bin[i] = 0;
	for (; ; ++ dec){
		int a = * dec;
		if (a == '\0')
			break;
		decimal_num = decimal_num * 10 + (a - '0');
	} 
	while (decimal_num != 0){
		converse[count] = decimal_num % 2;
		count ++;
		decimal_num /= 2;
	}

	for (int i = 0; i < count; ++ i) {
		bin[i] = converse [count - 1 - i];
		bin[i] = bin[i] + '0';
	}
}

