	long int sum=0;
	for (int i = 0; i <= lon; ++ i) {
		long int ci=1;
		for (int j = 0; j < lon-i-1; ++ j){ci=ci*16;}
		if(hexString[i]>=48&&hexString[i]<58) sum=sum+ci*(hexString[i]-48);
