}

void sort(char s[]){
	int length=strlen(s);
	int number[length];
	for (int i = 0; i <length; ++ i) {
		number[i]=tran(s[i]);
	}
	for (int j = 0; j <1000; ++ j) {
	for (int i = 0; i <length-1; ++ i) {
		if(number[i]>number[i+1]){
			int temp=number[i];
			number[i]=number[i+1];
			number[i+1]=temp;
		}
	}	
	}//骚操作
	for (int i = 0; i <length; ++ i) {
		s[i]=number[i]+'0';
	}
}

bool isAnagram(char s1[],char s2[]){
	sort(s1);
	sort(s2);
	int ss1=atoi(s1);
	int ss2=atoi(s2);
	if(ss1==ss2){
		return 1;
	}
	return 0;
}

