void sort_words(char * words[], int num_words) {
	int c=0;//c的存在就是使其充分比较
	for (int i = 0; c<100; ++ i) {
		if(i==num_words-1){
			i=0;
			++c;
		}//每次都是相邻比较换位置
		char *a=words[i];
		char *b=words[i+1];
		char *temp;
		while (*a==*b){
			*a++;
			*b++;
			if(*a== ' '){
				*a--;
				*b--;
				break;
			}
			else if(*b== ' '){
				*a--;
				*b--;
				temp=words[i];
			   words[i]=words[i+1];
			   words[i+1]=temp;
				break;
			}
		}
		if(*a>*b){
		   temp=words[i];
			words[i]=words[i+1];
			words[i+1]=temp;
		}
	}
}

