#define Max(x,y)(x>y?x:y)
int max_price_memory[100]={0};
int getMaxCuttingPrice(int prices[][2],int priceCount,int rodLength){
	if (max_price_memory[rodLength]>0)
		return max_price_memory[rodLength];
	int cur_max_price=(rodLength<=prices[priceCount-1][0]?prices[rodLength-1][1]:0);
	for (int i = 0; i <rodLength-1; ++ i) {
		int first_cut=i+1;
		int price=getMaxCuttingPrice(prices,priceCount,first_cut)+getMaxCuttingPrice(prices,priceCount,rodLength-first_cut);
		cur_max_price=Max(price,cur_max_price);
	}
	max_price_memory[rodLength]=cur_max_price;
	return cur_max_price;
}//prices里存储价格表 rodLength是木材长度 priceCount是指最长可销售木材长度+1

