int knapsack(int v[],int w[],int maxWeight)
{
	for(int i=0;i<10;i++)
		for(int j=1;j<=maxWeight;j++)
		{
			if(j>=w[i])
			{
			  if(dp[j]>=dp[j-w[i]]+v[i])
					dp[j]=dp[j];
			  else
					dp[j]=dp[j-w[i]]+v[i];
			}
	   }
	return dp[maxWeight];
}

