	for(int i = 0 ;i<k;++i){
		for(int j = 0 ; j<k;++j){
			if(array[i]<=array[j]){
				int temp = array[j];
				array[j]=array[i];
				array[i]=temp;
			}
		}
	}
}

int getMaxCuttingPrice(int prices[][2],int priceCount,int rodLength){
	int c_m_p=(rodLength<=priceCount?prices[rodLength-1][1]:0);
	for(int i = 0;i<rodLength-1;++i){
		int f_c = i + 1;
		int p = getMaxCuttingPrice(prices,priceCount,f_c)+getMaxCuttingPrice(prices,priceCount,rodLength-f_c);
		c_m_p = (p>c_m_p?p:c_m_p);
	}
	return c_m_p;

}
