int gcd(int x,int y){
	int result;
	if(x>y){
     int i=x-y;
		x=x-i;
		y=y+i;
	}
	for(int j=x-1;j>0;--j){
		if(x%j==0&&y%j==0){
			result=j;
			break;
		}
	}
	return result;
}

