    for(int i=0;i<10;i++){dim1[i]=0;dim2[i]=0;}
    if(n==1){min=0;return min;}
    else if(n>1){
        for(int i=1;i<n;i++){
            for(int j=0,m=0;j<=i;j++,m++){dim1[m]=dim[j];}
            num1=getMultiplications(i,dim1);
            for(int j=i,m=0;j<=n;j++,m++){dim2[m]=dim[j];}
            num2=getMultiplications(n-i,dim2);
            num3=dim[i-1]*dim[i]*dim[i+1];
            sum=num1+num2+num3;
            //printf("num1=%d num2=%d num3=%d sum=%d n=%d\n",num1,num2,num3,sum,n);
            min=minint(min,sum);
        }
    }
    return min;
}

