void sort_words(char * words[],int num_words){
	char * temp=NULL;
	for (int i = 1; i < num_words; ++ i) {
		if(*words[i]<*words[i-1]){
			temp=words[i-1];
			words[i-1]=words[i];
			words[i]=temp;
			for (int j = i-1; j>0 ; j--) {
				if(*words[j]<*words[j-1]){
					temp=words[j-1];
					words[j-1]=words[j];
					words[j]=temp;
				}
			}
		}else{continue;}
	}
}

