void insertionSort(int data[],int size){
	int temp=0;
	for (int i = 1; i < size; ++ i){
		if(data[i]<data[i-1]){
			temp=data[i-1];
			data[i-1]=data[i];
			data[i]=temp;
			for (int j=i-1; j>0; -- j){
				if(data[j]<data[j-1]){
					temp=data[j-1];
					data[j-1]=data[j];
					data[j]=temp;
				}
			}
		}else{continue;}
	}
}

