int gcd(int x,int y){
	if(x < y){
		int t;
		t = y;
		y = x;
		x = t;
	}
	int a;
	while(1){
		a = x - y;
		if(a > y){
			int m;
			m = y;
			y = a;
			a = m;
		}
		x = y;
		y = a;
		if(x == y) break;
	}
	return x;
}

int lcm(int j,int k){
	int x = j,y = k;
	if(x < y){
		int t;
		t = y;
		y = x;
		x = t;
	}
	int a;
	while(1){
		a = x - y;
		if(a > y){
			int m;
			m = y;
			y = a;
			a = m;
		}
		x = y;
		y = a;
		if(x == y) break;
	}
	return j * k / x;
}

