int getMultiplications(int n,int dimensions[]){
	int m[n+1][n+1];
	for (int i = 1; i <= n; ++ i){
		m[i][i] = 0;
	} 
	for (int i = 1; i < n; ++ i){ //i为当前需要相乘矩阵的数量
		for (int j = 1; j < n - i + 1; ++ j){
			int k = i + j;
			m[j][k] = m[j][j] + m[j+1][k] + dimensions[j - 1]*dimensions[j]*dimensions[k];
			for (int l = j + 1; l < k; ++ l){
				int temp = m[j][l] + m[l+1][k] + dimensions[j - 1]*dimensions[l]*dimensions[k];
				if(temp < m[j][k]){
					m[j][k] = temp;
				}
			} 
		}
	} 
	return m[1][n];
}

