#include <stdio.h>

int factorial(int n) {
	int res = 1;
	int i = 0;
	while (i < n) {
		i = i + 1;
		res = res * i;
	}
	return res;
}

int main() {
	int n;
	scanf("%d", &n);
	printf("%d\n", factorial(n));
}

