#include <stdio.h>

int readline(char s[], int limit) {
	for (int i = 0; i < limit; ++ i) {
		int c = getchar();
		s[i] = c;
		if (i == limit - 1 || c == '\n' || c == EOF) {
			s[i] = '\0';
			return i;
		}
	}
	return -1;
}

