/*
This function input a real number and outputs its 
integer part and fraction part, respectively.
*/
int main() {
	// read a real number 
	double number;
	scanf("%lf", &number); 

	// obtain its integer part by 
	// converting to an intger 
	int integer = number;

	// obtain its fraction part by 
	// reducing its integer part
	number -= integer;

	// output the two parts
	printf("%d\n", integer);
	printf("%lf\n", number);
}

#include <stdio.h>

/*
This function inputs an integer and output its last binary digit and its second last binary digit.
*/
int main() {
	// input the number 
	int number;
	scanf("%d", &number);
	// output its last binary digit and an empty space
	printf("%d", number % 2);
	// make the second last binary digit the last binary digit
	number /= 2;
	// output the new last (the orginal second last) binary digit
	printf(" %d", number % 2); 
}

