/*
cos(x) ≈ 1 - x^2/2! + x^4/4! - x^6/6!
*/
double cos(double x) {
	double x2 = x * x;
	double x4 = x2 * x2;
	double x6 = x4 * x2;
	double fab2 = 2;
	double fab4 = fab2 * 3 * 4;
	double fab6 = fab4 * 5 * 6;
	double cose = 1 - x2 / fab2 + x4 / fab4 - x6 / fab6;
	return cose;
}

