int min_(double array[], int size) {
	int index = 0;
	for (int i = 1; i < size; ++ i) {
		if (array[index] > array[i])
			index = i;
	}
	return index;
}

int max_(double array[], int size) {
	int index = 0;
	for (int i = 1; i < size; ++ i) {
		if (array[index] < array[i])
			index = i;
	}
	return index;
}

void sort(double array[]) {
	int min_index = min_(array, 3);
	int max_index = max_(array, 3);
	int rest_index = 0;
	if (min_index == 0 || max_index == 0) {
		rest_index = 1;
		if (min_index == 1 || max_index == 1) rest_index = 2;
	}
	int min = array[min_index];
	int max = array[max_index];
	int rest = array[rest_index];
	array[0] = min;
	array[1] = max;
	array[2] = rest;
}

