int index_of(int array[], int size, int value) {
	for (int i = 0; i < size; ++ i) {
		if (value == array[i]) return i;
	}
	return -1;
}

int make_set(int array[], int size) {
	int set_size = 0;
	for (int i = 0; i < size; ++ i) {
		if (index_of(array, set_size, array[i]) == -1) {
			array[set_size] = array[i];
			++ set_size;
		}
	}
	return set_size;
}

