int gcd1(int m, int n) {
	int min = m;
	if (min > n) min = n;
	for (int i = min; i >= 1; -- i) {
		if (m % i == 0 && n % i == 0) return i;
	}
	return 1;
}

int gcd2(int m, int n) {
	while (m != n) {
		while (m > n) {
			m -= n;
		}
		// swap m an n
		int temp = m;
		m = n;
		n = temp;
	}
	return m;
}

