#define L(i, n) for (int i = 0; i < n; ++ i)
#define or ||
#define and &&
#define not !
#define elif else if

void print_pattern(int n) {
	L(i, n) {
		L(j, n) {
			if (i == j or i + j == n - 1) putchar('Y');
			elif (i < j and i + j < n - 1 or i > j and i + j > n - 1) putchar('G');
			else putchar('B');
		}
		putchar('\n');
	}
}

