unsigned long strlen(const char text[]) {
	for (int i = 0; true; ++ i) {
		if (text[i] == 0) return i;
	}
}

char * strcpy(char to[], const char from[]) {
	for (int i = 0; true; ++ i) {
		to[i] = from[i];
		if (from[i] == 0) break;
	}
	return to;
}

char * strcat(char to[], const char from[]) {
	int length = strlen(to);
	strcpy(&to[length], from);
	return to;
}

int strcmp(const char s1[], const char s2[]) {
	for (int i = 0; true; ++ i) {
		int diff = s1[i] - s2[i];
		if (diff != 0) return diff;
		if (s1[i] == 0) return 0; 
	}
}

int atoi(const char text[]) {
	bool negative = (text[0] == '-');
	int value = 0;
	int i = (negative ? 1 : 0);
	for (; i < strlen(text); ++ i) {
		value = value * 10 + (text[i] - '0');
	}
	return (negative ? -value : value);
}

void reverse(char text[]) {
	int length = strlen(text);
	for (int i = 0; i < length / 2; ++ i) {
		char temp = text[i];
		text[i] = text[length - 1 - i];
		text[length - 1 - i] = temp;
	}
}

void _itoaPositive(int value, char text[]) {
	int i = 0;
	for (; value > 0; ++ i) {
		text[i] = value % 10 + '0';
		value /= 10;
	}
	text[i] = 0;
	reverse(text);
}

void itoa(int value, char text[]) {
	if (value == 0) {
		text[0] = '0';
		text[1] = 0;
	}
	else if (value > 0) {
		_itoaPositive(value, text);
	}
	else {
		text[0] = '-';
		_itoaPositive(-value, &text[1]);
	}
}

void substring(char to[], const char text[], 
				int start, int len) 
{
	for (int i = 0; i < len; ++ i) {
		to[i] = text[start + i];
	}
	to[len] = 0;
}

void insert(char to[], int at, const char ins[]) {
	int lenTo = strlen(to);
	int lenIns = strlen(ins);
	for (int i = 0; i < lenTo - at; ++ i) {
		to[lenTo + lenIns - 1 - i] = 
			to[lenTo - 1 - i];
	}
	to[lenTo + lenIns] = 0;
	for (int i = 0; i < lenIns; ++ i) {
		to[at + i] = ins[i];
	}
}

