	char line[1000];
	inputString(line, 1000);
	int length = strlen(line);

	int start = 0;
	for (int i = 0; i < max; ++ i) {
		start = indexOf(line, "-0123456789", start);
		if (start == -1) return i;

		int end = indexOf(line, " ", start);
		if (end >= 0) {
			line[end] = 0;
		}
		else {
			end = length;
		}

		data[i] = atoi(&line[start]);

		start = end + 1;
		if (start >= length) return i + 1;
	}
	return max;
}

