		if (text[i] == 0) return i;
	}
}

int atoi(const char text[]) {
	bool negative = (text[0] == '-');
	int value = 0;
	int i = (negative ? 1 : 0);
	for (; i < strlen(text); ++ i) {
		value = value * 10 + (text[i] - '0');
	}
	return (negative ? -value : value);
}

bool isSpace(char c) {
	return (c == ' ' || c == '\r' || c == '\t' || c == '\n');
}

bool isInSet(const char set[], char c) {
	int len = strlen(set);
	for (int i = 0; i < len; ++ i) {
		if (c == set[i]) return true;
	}
	return false;
}

int indexOf(const char line[], const char set[], int start) {
	for (int i = start; true; ++ i) {
		if (line[i] == 0) return -1;
		if (isInSet(set, line[i])) return i;
	}
}

int indexOfInt(const int data[], int size, int to_find) {
	for (int i = 0; i < size; ++ i) {
		if (to_find == data[i]) return i;
	}
	return -1;
}

