int readLineDistinct(int data[], int max) {
	char line[1000];
	inputString(line, 1000);
	int length = strlen(line);

	int start = 0;
	int count = 0;
	while (count < max) {
		start = indexOf(line, "-0123456789", start);
		if (start == -1) return count;

		int end = indexOf(line, " ", start);
		if (end >= 0) {
			line[end] = 0;
		}
		else {
			end = length;
		}

		//cout << &line[start] << endl;
		int val = atoi(&line[start]);
		if (indexOfInt(data, count, val) == -1) {
			data[count] = val;
			++ count;
		}

		start = end + 1;
		if (start >= length) return count;
	}
	return max;
}

