void stringCopy(char to[], const char from[]) { 
    for (int i = 0; ; ++ i) { 
        to[i] = from[i]; 
        if (to[i] == 0) break; 
    } 
} 

void stringReverse(char s[]) { 
    int length = stringLength(s); 
    for (int i = 0; i < length / 2; ++ i) { 
        char temp = s[i]; 
        s[i] = s[length - i - 1]; 
        s[length - i - 1] = temp; 
    } 
} 

void longToArray(long value, char s[], int base) { 
    if (value == 0) { 
        stringCopy(s, "0"); 
    } 
    int length = 0; 
    while (value > 0) { 
        int digit = value % base; 
        value = value / base; 
        if (digit < 10) digit += '0'; 
        else digit += ('A' - 10); 
        s[length] = digit; 
        ++ length; 
    } 
    s[length] = 0; 
    stringReverse(s); 
}

