int maxIndex(char s[], int length) {
	int index = 0;
	int i;
	for (i = 1; i < length; ++ i) {
		if (s[i] > s[index]) {
			index = i;
		}
	}
	return index;
}

void swapCharArrayElements(char s[], int index1, int index2) {
	char temp = s[index1];
	s[index1] = s[index2];
	s[index2] = temp;
}

void selectionSort(char s[], int length) {
	if (length == 1) return;
	int index = maxIndex(s, length);
	swapCharArrayElements(s, index, length - 1);
	selectionSort(s, length - 1);
}

void sort(char s[]) {
	selectionSort(s, stringLength(s));
}

