int _getInsertionPosition(const char s[], int length, char toIns) {
	int i;
    for (i = 0; i < length; ++ i) { 
        if (s[i] > toIns) return i;
    }
	return length;
}

void _moveChars(char s[], int from, int to, int steps) {
	int i;
	if (steps < 0) {
		for (i = from; i <= to; ++ i) {
			s[i + steps] = s[i];
		}
	}
	else {
		for (i = to; i >= from; -- i) {
			s[i + steps] = s[i];
		}
	}
}

void _insert(char s[], int length, char toIns) {
	int insIndex = _getInsertionPosition(s, length, toIns);
	_moveChars(s, insIndex, length - 1, 1);
	s[insIndex] = toIns;
}

void insertionSort(char s[], int length) {
	if (length <= 1) return;
	insertionSort(s, length - 1);
	_insert(s, length - 1, s[length - 1]);
}

