void sort(char s[]) {
	insertionSort(s, strlen(s));
}

bool isAnagram(const char s1[], const char s2[]) {
	char s3[100];
	char s4[100];
	strcpy(s3, s1);
	strcpy(s4, s2);
	sort(s3);
	sort(s4);
	return 0 == strcmp(s3, s4);
}

